definition.script:
def stepsForParallel = [:]

def dbNames = ['nconf', 'phpmyadmin', 'zen']
for (int i=0; i<dbNames.size(); ++i) {
   def dbName = dbNames[i]
   def opfile = "${dbName}.sql"
   stepsForParallel[dbName] =  {
        stage("${dbName}") {
            steps {
                sh "sudo docker exec -i mysql mysqldump --user bobb --password=${PWRD}  ${dbName} > ${dbName}.sql"
                archive opfile
                stash includes: opfile, name: dbName
            }
        }
    }
}    
stepsForParallel[failFast] = true


// return a closure because we do not want 'node(..){...}' execcuted when this function is called
def transformIntoStep(dbName) {
  return {
  }
}


pipeline {
   agent { label 'ubuntu-s3' }
   options { timestamps() }
   stages {
        stage ('Data Collection') {
        // Actually run the steps in parallel - parallel takes a map as an argument, hence the above.
            parallel nconf: {
                sh "sudo docker exec -i mysql mysqldump --user bobb --password=${PWRD} nconf > nconf.sql"
                archive opfile
                stash includes: opfile, name: nconf
            }, phpmyadmin: {
                sh "sudo docker exec -i mysql mysqldump --user bobb --password=${PWRD} phpmyadmin > phpmyadmin.sql"
                archive opfile
                stash includes: opfile, name: phpmyadmin
            }, zen: {
                sh "sudo docker exec -i mysql mysqldump --user bobb --password=${PWRD} zen > zen.sql"
                archive opfile
                stash includes: opfile, name: zen
            }
        }
    }

    stage ('Update GIThub') {
        steps {
            checkout scm
            unstash nconf
            unstash phpmyadmin
            unstash zen
            archive includes:'*.sql'
            sh 'git add -A; git comment -m "mysql DB updates"; git push'
        }
    }
}
