definition.script:
pipeline {
	agent { label 'ubuntu-s3' }
	options { timestamps() }
	step ('nconf') {
		sh "sudo docker exec -i mysql mysqldump --user bobb --password=${PWRD} nconf > nconf.sql"
		archive opfile
		stash includes: opfile, name: nconf
	}
	step ('phpmyadmin') {
		sh "sudo docker exec -i mysql mysqldump --user bobb --password=${PWRD} phpmyadmin > phpmyadmin.sql"
		archive opfile
		stash includes: opfile, name: phpmyadmin
	}
	step ('zen') {
		sh "sudo docker exec -i mysql mysqldump --user bobb --password=${PWRD} zen > zen.sql"
		archive opfile
		stash includes: opfile, name: zen
	}
	step ('Update GIThub') {
		checkout scm
		unstash nconf
		unstash phpmyadmin
		unstash zen
		archive includes:'*.sql'
		sh 'git add -A; git comment -m "mysql DB updates"; git push'
	}
}
